//SPDX-License-Identifier: UNLICENSED
pragma solidity >=0.4.22 <0.9.0;

contract SocialMedia {
  uint public totalPosts = 0;

  address socialMediaOwner;

  struct Post {
    uint id;
    string content;
    uint256 donated;
    address owner;
  }

  constructor() public {
    socialMediaOwner = msg.sender;
  }

  event Withdraw(address indexed from, address indexed to, uint amount);

  mapping (address => uint256) public balances;
  mapping (uint => Post) public posts;

  function createPost(string memory _content) public {
    totalPosts++;
    posts[totalPosts] = Post(totalPosts, _content, 0, msg.sender);
  }

  // function buyCredit(uint256 _amount) payable public {
  //   require(_amountToDonate > 0, "You need to send some ether");
  //   balances[msg.sender] += msg.value;
  // }

  // function donate(uint _postId) payable public returns(bool success) {
  //   uint256 _amountToDonate = msg.value;
  //   require(_amountToDonate > 0, "You need to send some ether");
  //   Post memory _post = posts[_postId];
  //   _post.donated += msg.value;
  //   posts[_postId] = _post;
  //   balances[_post.owner] += msg.value;
  //   return true;
  // }

  function donate(uint _postId) payable public returns(bool success) {
    uint256 _amountToDonate = msg.value;
    require(_amountToDonate > 0, "You need to send some ether");
    Post memory _post = posts[_postId];
    _post.donated += msg.value;
    posts[_postId] = _post;
    balances[_post.owner] += msg.value;
    return true;
  }

  function withdraw(uint256 _amount) public returns(bool success) {
    require(_amount > 0, "You need to withdraw at atleast some tokens");
    require(_amount <= balances[msg.sender], "Insufficient");
    balances[msg.sender] -= _amount;
    uint256 _amountToDeduct = _amount * 10 / 100;
    address payable wallet = address(uint160(address(msg.sender)));
    wallet.transfer(_amount - _amountToDeduct);
    emit Withdraw(socialMediaOwner, msg.sender, _amount);
    return true;
  }
}
